import React from 'react';
import { Code, Database, Server, Smartphone, Palette } from 'lucide-react';

const Skills = () => {
  const skills = [
    { name: 'React', icon: Code, color: 'bg-blue-500' },
    { name: 'Tailwind CSS', icon: Palette, color: 'bg-cyan-500' },
    { name: 'JavaScript', icon: Code, color: 'bg-yellow-500' },
    { name: 'HTML/CSS', icon: Palette, color: 'bg-orange-500' },
    { name: 'Spring Boot', icon: Server, color: 'bg-green-500' },
    { name: 'MySQL', icon: Database, color: 'bg-blue-600' },
    { name: 'Docker', icon: Server, color: 'bg-blue-400' },
    { name: 'JWT', icon: Smartphone, color: 'bg-purple-500' },
  ];

  return (
    <div className="py-20 px-4 max-w-6xl mx-auto">
      <h2 className="text-4xl font-bold text-center mb-12 text-slate-100">Skills & Technologies</h2>
      <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
        {skills.map((skill, index) => (
          <div
            key={index}
            className="bg-slate-800 border border-slate-600 rounded-lg p-6 text-center hover:scale-105 transition-transform duration-300 hover:border-blue-500 group"
          >
            <div className={`w-16 h-16 ${skill.color} rounded-full flex items-center justify-center mx-auto mb-4 group-hover:scale-110 transition-transform duration-300`}>
              <skill.icon size={32} className="text-white" />
            </div>
            <h3 className="text-lg font-semibold text-slate-200">{skill.name}</h3>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Skills;
