import React, { useEffect, useState } from 'react';
import Hero from './Hero';
import About from './About';
import Skills from './Skills';
import Projects from './Projects';
import Contact from './Contact';
import Star from './Star';
import { Menu, X } from 'lucide-react';

const Portfolio = () => {
  const [visibleSections, setVisibleSections] = useState({});
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const sections = ['hero', 'about', 'skills', 'projects', 'contact'];
      const newVisibleSections = {};

      sections.forEach(section => {
        const element = document.getElementById(section);
        if (element) {
          const rect = element.getBoundingClientRect();
          newVisibleSections[section] = rect.top < window.innerHeight * 0.8;
        }
      });

      setVisibleSections(newVisibleSections);
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Initial check

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    setIsMenuOpen(false);
  };

  return (
    <div className="relative min-h-screen bg-dark-blue text-slate-50 overflow-x-hidden">
      {/* Navigation */}
      <nav className="fixed top-0 w-full bg-slate-900/90 backdrop-blur-sm z-40 border-b border-slate-700">
        <div className="max-w-6xl mx-auto px-4 py-4 flex justify-between items-center">
          <h1 className="text-xl font-bold bg-gradient-to-r from-blue-400 to-blue-600 bg-clip-text text-transparent">
            John Doe
          </h1>
          <div className="hidden md:flex space-x-8">
            <button onClick={() => scrollToSection('hero')} className="hover:text-blue-400 transition-colors">Home</button>
            <button onClick={() => scrollToSection('about')} className="hover:text-blue-400 transition-colors">About</button>
            <button onClick={() => scrollToSection('skills')} className="hover:text-blue-400 transition-colors">Skills</button>
            <button onClick={() => scrollToSection('projects')} className="hover:text-blue-400 transition-colors">Projects</button>
            <button onClick={() => scrollToSection('contact')} className="hover:text-blue-400 transition-colors">Contact</button>
          </div>
          <button className="md:hidden" onClick={() => setIsMenuOpen(!isMenuOpen)}>
            {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>
        {isMenuOpen && (
          <div className="md:hidden bg-slate-800 border-t border-slate-700">
            <div className="px-4 py-4 space-y-4">
              <button onClick={() => scrollToSection('hero')} className="block hover:text-blue-400 transition-colors">Home</button>
              <button onClick={() => scrollToSection('about')} className="block hover:text-blue-400 transition-colors">About</button>
              <button onClick={() => scrollToSection('skills')} className="block hover:text-blue-400 transition-colors">Skills</button>
              <button onClick={() => scrollToSection('projects')} className="block hover:text-blue-400 transition-colors">Projects</button>
              <button onClick={() => scrollToSection('contact')} className="block hover:text-blue-400 transition-colors">Contact</button>
            </div>
          </div>
        )}
      </nav>

      {/* Animated Stars Background */}
      <div className="fixed inset-0 z-0">
        {Array.from({ length: 100 }, (_, i) => (
          <Star key={i} delay={i * 0.1} />
        ))}
      </div>

      {/* Main Content */}
      <div className="relative z-10">
        <section id="hero" className={`transition-all duration-1000 pt-20 ${visibleSections.hero ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`}>
          <Hero />
        </section>
        <section id="about" className={`transition-all duration-1000 ${visibleSections.about ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`}>
          <About />
        </section>
        <section id="skills" className={`transition-all duration-1000 ${visibleSections.skills ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`}>
          <Skills />
        </section>
        <section id="projects" className={`transition-all duration-1000 ${visibleSections.projects ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`}>
          <Projects />
        </section>
        <section id="contact" className={`transition-all duration-1000 ${visibleSections.contact ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}`}>
          <Contact />
        </section>
      </div>
    </div>
  );
};

export default Portfolio;
