import React from 'react';
import { Code, Palette, Database, Server, Smartphone, Zap, GitBranch, Cloud } from 'lucide-react';

const Skills = () => {
  const skills = [
    { name: 'React', icon: Code, color: 'bg-blue-500', level: 95 },
    { name: 'Tailwind CSS', icon: Palette, color: 'bg-cyan-500', level: 90 },
    { name: 'JavaScript', icon: Zap, color: 'bg-yellow-500', level: 92 },
    { name: 'Node.js', icon: Server, color: 'bg-green-500', level: 88 },
    { name: 'MySQL', icon: Database, color: 'bg-blue-600', level: 85 },
    { name: 'Git', icon: GitBranch, color: 'bg-orange-500', level: 90 },
    { name: 'Docker', icon: Server, color: 'bg-blue-400', level: 80 },
    { name: 'AWS', icon: Cloud, color: 'bg-purple-500', level: 75 },
  ];

  return (
    <div className="py-20 px-4 max-w-6xl mx-auto">
      <div className="text-center mb-16">
        <h2 className="text-4xl font-bold mb-6 text-slate-100">Skills & Expertise</h2>
        <p className="text-lg text-slate-400 max-w-2xl mx-auto">
          Technologies and tools I use to bring ideas to life
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {skills.map((skill, index) => (
          <div
            key={index}
            className="bg-slate-800/50 border border-slate-700 rounded-lg p-6 hover:scale-105 transition-all duration-300 hover:border-blue-500 group"
          >
            <div className="flex items-center mb-4">
              <div className={`w-12 h-12 ${skill.color} rounded-lg flex items-center justify-center mr-4 group-hover:scale-110 transition-transform duration-300`}>
                <skill.icon size={24} className="text-white" />
              </div>
              <div>
                <h3 className="text-lg font-semibold text-slate-200">{skill.name}</h3>
                <p className="text-sm text-slate-400">{skill.level}% Proficiency</p>
              </div>
            </div>
            <div className="w-full bg-slate-700 rounded-full h-2">
              <div
                className={`h-2 ${skill.color} rounded-full transition-all duration-1000 ease-out`}
                style={{ width: `${skill.level}%` }}
              ></div>
            </div>
          </div>
        ))}
      </div>

      <div className="mt-16 text-center">
        <h3 className="text-2xl font-semibold mb-8 text-slate-200">Additional Technologies</h3>
        <div className="flex flex-wrap justify-center gap-4">
          {['TypeScript', 'MongoDB', 'PostgreSQL', 'Redis', 'GraphQL', 'REST APIs', 'JWT', 'OAuth', 'WebSockets', 'PWA'].map((tech, index) => (
            <span key={index} className="px-4 py-2 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-full text-sm font-medium hover:from-blue-500 hover:to-purple-500 transition-all duration-300 transform hover:scale-105">
              {tech}
            </span>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Skills;
